10.5 動態列表操作
import React, { useState } from 'react';

function TodoApp() {
  const [todos, setTodos] = useState([
    { id: 1, text: '學習 React Hooks', completed: false, priority: 'high' },
    { id: 2, text: '建立 Todo 應用', completed: true, priority: 'medium' },
    { id: 3, text: '部署到生產環境', completed: false, priority: 'low' }
  ]);

  const [newTodo, setNewTodo] = useState('');
  const [filter, setFilter] = useState('all'); // all, active, completed
  const [sortBy, setSortBy] = useState('id'); // id, priority, text

  // 新增 Todo
  const addTodo = () => {
    if (newTodo.trim()) {
      const todo = {
        id: Date.now(),
        text: newTodo.trim(),
        completed: false,
        priority: 'medium'
      };
      setTodos([...todos, todo]);
      setNewTodo('');
    }
  };

  // 切換完成狀態
  const toggleTodo = (id) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  // 刪除 Todo
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  // 更新優先級
  const updatePriority = (id, priority) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, priority } : todo
    ));
  };

  // 過濾 Todos
  const getFilteredTodos = () => {
    let filtered = todos;

    switch (filter) {
      case 'active':
        filtered = todos.filter(todo => !todo.completed);
        break;
      case 'completed':
        filtered = todos.filter(todo => todo.completed);
        break;
      default:
        filtered = todos;
    }

    // 排序
    return filtered.sort((a, b) => {
      switch (sortBy) {
        case 'priority':
          const priorityOrder = { high: 3, medium: 2, low: 1 };
          return priorityOrder[b.priority] - priorityOrder[a.priority];
        case 'text':
          return a.text.localeCompare(b.text);
        default:
          return a.id - b.id;
      }
    });
  };

  const priorityColors = {
    high: '#ffebee',
    medium: '#fff3e0',
    low: '#e8f5e8'
  };

  const priorityLabels = {
    high: '高',
    medium: '中',
    low: '低'
  };

  return (

      Todo 應用程式

      {/* 新增 Todo */}

         setNewTodo(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && addTodo()}
          placeholder="輸入新的待辦事項..."
          style={{
            padding: '8px',
            width: '70%',
            marginRight: '10px',
            border: '1px solid #ddd',
            borderRadius: '4px'
          }}
        />

          新增



      {/* 過濾和排序控制 */}


          過濾: 
           setFilter(e.target.value)}>
            全部
            未完成
            已完成



          排序: 
           setSortBy(e.target.value)}>
            建立順序
            優先級
            名稱




      {/* Todo 列表 */}

        {getFilteredTodos().length === 0 ? (

            {filter === 'all' ? '沒有待辦事項' : `沒有${filter === 'active' ? '未完成' : '已完成'}的事項`}

        ) : (
          getFilteredTodos().map(todo => (

               toggleTodo(todo.id)}
                style={{ marginRight: '10px' }}
              />


                {todo.text}


               updatePriority(todo.id, e.target.value)}
                style={{ margin: '0 10px', padding: '2px' }}
                disabled={todo.completed}
              >
                高優先級
                中優先級
                低優先級



                {priorityLabels[todo.priority]}


               deleteTodo(todo.id)}
                style={{
                  padding: '4px 8px',
                  backgroundColor: '#f44336',
                  color: 'white',
                  border: 'none',
                  borderRadius: '3px',
                  cursor: 'pointer',
                  fontSize: '0.8em'
                }}
              >
                刪除


          ))
        )}


      {/* 統計資訊 */}

        總計: {todos.length} 項 | 
        已完成: {todos.filter(t => t.completed).length} 項 | 
        未完成: {todos.filter(t => !t.completed).length} 項


  );
}

export default TodoApp;

10.6 巢狀列表
當處理巢狀資料結構時，每一層都需要適當的 Keys：
