11.3 表單驗證
import React, { useState } from 'react';

function FormValidation() {
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    password: '',
    confirmPassword: '',
    age: '',
    website: ''
  });

  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  // 驗證規則
  const validateField = (name, value) => {
    switch (name) {
      case 'email':
        if (!value) return '電子信箱為必填欄位';
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) return '請輸入有效的電子信箱';
        return '';

      case 'username':
        if (!value) return '使用者名稱為必填欄位';
        if (value.length  120) return '請輸入有效的年齡 (1-120)';
        return '';

      case 'website':
        if (value && !/^https?:\/\/.+/.test(value)) return '請輸入有效的網址 (需包含 http:// 或 https://)';
        return '';

      default:
        return '';
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // 即時驗證
    if (touched[name]) {
      const error = validateField(name, value);
      setErrors(prev => ({
        ...prev,
        [name]: error
      }));
    }
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;

    setTouched(prev => ({
      ...prev,
      [name]: true
    }));

    const error = validateField(name, value);
    setErrors(prev => ({
      ...prev,
      [name]: error
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // 驗證所有欄位
    const newErrors = {};
    const newTouched = {};

    Object.keys(formData).forEach(key => {
      newTouched[key] = true;
      const error = validateField(key, formData[key]);
      if (error) newErrors[key] = error;
    });

    setTouched(newTouched);
    setErrors(newErrors);

    // 如果沒有錯誤，提交表單
    if (Object.keys(newErrors).length === 0) {
      alert('表單驗證通過！資料已提交。');
      console.log('提交的資料:', formData);
    }
  };

  const getFieldStyle = (fieldName) => ({
    width: '100%',
    padding: '8px',
    marginTop: '5px',
    border: `1px solid ${errors[fieldName] && touched[fieldName] ? '#f44336' : '#ddd'}`,
    borderRadius: '4px',
    backgroundColor: errors[fieldName] && touched[fieldName] ? '#ffebee' : 'white'
  });

  const isFormValid = Object.keys(formData).every(key => 
    !validateField(key, formData[key]) && formData[key] !== ''
  );

  return (

      表單驗證範例


          電子信箱 *:

          {errors.email && touched.email && (

              {errors.email}

          )}



          使用者名稱 *:

          {errors.username && touched.username && (

              {errors.username}

          )}



          密碼 *:

          {errors.password && touched.password && (

              {errors.password}

          )}



          確認密碼 *:

          {errors.confirmPassword && touched.confirmPassword && (

              {errors.confirmPassword}

          )}



          年齡 *:

          {errors.age && touched.age && (

              {errors.age}

          )}



          個人網站 (選填):

          {errors.website && touched.website && (

              {errors.website}

          )}



          提交註冊



          表單狀態:
          表單有效: {isFormValid ? '✓ 是' : '✗ 否'}
          錯誤數量: {Object.keys(errors).filter(key => errors[key]).length}
          已驗證欄位: {Object.keys(touched).filter(key => touched[key]).length}/{Object.keys(formData).length}



  );
}

export default FormValidation;

