function ShoppingApp() {
  const [products] = useState([
    { id: 1, name: 'iPhone 15', price: 999, stock: 5 },
    { id: 2, name: 'MacBook Pro', price: 1999, stock: 3 },
    { id: 3, name: 'AirPods', price: 179, stock: 0 },
